    function getAllStudents() external view returns (address[] memory);

    function getActiveStudents() external view returns (address[] memory);

    function deactivateStudent(address studentAddress) external;

    function reactivateStudent(address studentAddress) external;

    function recordFunding(address studentAddress, uint256 amount) external;

    function isStudentActive(address studentAddress) external view returns (bool);
}

contracts/LosslessVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/extensions/ERC4626.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./MockAavePool.sol";
import "./YieldSplitter.sol";

/**
 * @title LosslessVault
 * @notice ERC-4626 vault that generates yield via MockAavePool - donors keep principal, donate 100% yield
 * @dev Deposits go to MockAavePool, yield is harvested and sent to YieldSplitter
 */
contract LosslessVault is ERC4626, Ownable {
    // MockAavePool for yield generation
    MockAavePool public immutable aavePool;

    // YieldSplitter for yield distribution
    YieldSplitter public immutable yieldSplitter;

    // Events
    event YieldHarvested(uint256 amount, address indexed to);
    event PrincipalDepositedToPool(uint256 amount);
    event PrincipalWithdrawnFromPool(uint256 amount);

    /**
     * @notice Initialize LosslessVault
     * @param asset_ Address of underlying asset (USDC)
     * @param name_ Vault name
     * @param symbol_ Vault share symbol
     * @param _aavePool Address of MockAavePool
     * @param _yieldSplitter Address of YieldSplitter
     */
    constructor(
        IERC20 asset_,
        string memory name_,
        string memory symbol_,
        address _aavePool,
        address _yieldSplitter
    ) ERC4626(asset_) ERC20(name_, symbol_) Ownable(msg.sender) {
        require(_aavePool != address(0), "Invalid aave pool address");
        require(_yieldSplitter != address(0), "Invalid yield splitter address");

        aavePool = MockAavePool(_aavePool);
        yieldSplitter = YieldSplitter(_yieldSplitter);
    }

    /**
     * @notice Deposit assets and receive shares
     * @dev Overrides ERC4626 to deposit principal to MockAavePool
     */
    function deposit(uint256 assets, address receiver) public override returns (uint256) {
        uint256 shares = super.deposit(assets, receiver);

        // Deposit principal to MockAavePool
        IERC20(asset()).approve(address(aavePool), assets);
        aavePool.supply(assets, address(this));

        emit PrincipalDepositedToPool(assets);

        return shares;
    }

    /**
     * @notice Mint shares for specified assets
     * @dev Overrides ERC4626 to deposit principal to MockAavePool
     */
    function mint(uint256 shares, address receiver) public override returns (uint256) {
        uint256 assets = super.mint(shares, receiver);

        // Deposit principal to MockAavePool
        IERC20(asset()).approve(address(aavePool), assets);
        aavePool.supply(assets, address(this));

        emit PrincipalDepositedToPool(assets);

        return assets;
    }

    /**
     * @notice Withdraw assets and burn shares
     * @dev Overrides ERC4626 to withdraw principal from MockAavePool
     */
    function withdraw(uint256 assets, address receiver, address owner) public override returns (uint256) {
        uint256 shares = super.withdraw(assets, receiver, owner);

        // Withdraw principal from MockAavePool
        aavePool.withdraw(assets, address(this));

        emit PrincipalWithdrawnFromPool(assets);

        return shares;
    }

    /**
     * @notice Redeem shares for assets
     * @dev Overrides ERC4626 to withdraw principal from MockAavePool
     */
    function redeem(uint256 shares, address receiver, address owner) public override returns (uint256) {
        uint256 assets = super.redeem(shares, receiver, owner);

        // Withdraw principal from MockAavePool
        aavePool.withdraw(assets, address(this));

        emit PrincipalWithdrawnFromPool(assets);

        return assets;
    }

    /**
     * @notice Calculate total assets (principal in pool + any yield)
     * @dev Returns the balance in MockAavePool
     */
    function totalAssets() public view override returns (uint256) {
        return aavePool.getUserSupply(address(this));
    }

    /**
     * @notice Harvest yield from MockAavePool and send to YieldSplitter
     * @dev Only owner can call this (typically called by admin or keeper)
     */
    function harvestYield() external onlyOwner {
        // Harvest yield from MockAavePool
        uint256 yieldAmount = aavePool.harvestYield(address(yieldSplitter));

        require(yieldAmount > 0, "No yield to harvest");

        // Split yield 50/50 via YieldSplitter
        yieldSplitter.splitYield();

        emit YieldHarvested(yieldAmount, address(yieldSplitter));
    }

    /**
     * @notice Get available yield in MockAavePool
     * @return Available yield amount
     */
    function getAvailableYield() external view returns (uint256) {
        return aavePool.getAvailableYield();
    }

    /**
     * @notice Get total assets in MockAavePool (principal + yield)
     * @return Total assets
     */
    function getTotalPoolAssets() external view returns (uint256) {
        return aavePool.getTotalAssets();
    }
}

contracts/MockAavePool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title MockAavePool
 * @notice Mock Aave Pool for hackathon demo - simulates Aave V3 supply/withdraw functionality
 * @dev Allows instant yield generation via adminAddYield for demo purposes
 */
contract MockAavePool is Ownable {
    // Asset token (USDC)
    IERC20 public immutable asset;

    // User balances: user => balance
    mapping(address => uint256) public userSupplies;

    // Total supply in the pool
    uint256 public totalSupply;

    // Accumulated yield (for demo purposes)
    uint256 public accumulatedYield;

    // Events
    event Supply(address indexed user, uint256 amount, uint256 timestamp);
    event Withdraw(address indexed user, uint256 amount, uint256 timestamp);
    event YieldAdded(uint256 amount, uint256 timestamp);

    /**
     * @notice Initialize MockAavePool
     * @param _asset Address of the asset token (USDC)
     */
    constructor(address _asset) Ownable(msg.sender) {
        require(_asset != address(0), "Invalid asset address");
        asset = IERC20(_asset);
    }

    /**
     * @notice Supply assets to the pool (simulates Aave supply)
     * @param amount Amount of assets to supply
     * @param onBehalfOf Address to credit the supply to
     */
    function supply(uint256 amount, address onBehalfOf) external {
        require(amount > 0, "Amount must be greater than 0");
        require(onBehalfOf != address(0), "Invalid onBehalfOf address");

        // Transfer assets from caller to pool
        require(asset.transferFrom(msg.sender, address(this), amount), "Transfer failed");

        // Update balances
        userSupplies[onBehalfOf] += amount;
        totalSupply += amount;

        emit Supply(onBehalfOf, amount, block.timestamp);
    }

    /**
     * @notice Withdraw assets from the pool (simulates Aave withdraw)
     * @param amount Amount of assets to withdraw
     * @param to Address to send the assets to
     * @return Amount actually withdrawn
     */
    function withdraw(uint256 amount, address to) external returns (uint256) {
        require(amount > 0, "Amount must be greater than 0");
        require(to != address(0), "Invalid to address");
        require(userSupplies[msg.sender] >= amount, "Insufficient balance");

        // Update balances
        userSupplies[msg.sender] -= amount;
        totalSupply -= amount;

        // Transfer assets
        require(asset.transfer(to, amount), "Transfer failed");

        emit Withdraw(msg.sender, amount, block.timestamp);
        return amount;
    }

    /**
     * @notice Get user's supply balance
     * @param user Address to check
     * @return User's supply balance
     */
    function getUserSupply(address user) external view returns (uint256) {
        return userSupplies[user];
    }

    /**
     * @notice Admin function to add yield instantly (for demo purposes)
     * @dev Mints new tokens to simulate yield generation
     * @param amount Amount of yield to add
     */
    function adminAddYield(uint256 amount) external onlyOwner {
        require(amount > 0, "Amount must be greater than 0");
        require(totalSupply > 0, "No supply to generate yield on");

        // For demo: we'll need the asset to have a mint function
        // This assumes MockUSDC or similar has mint capability
        // In production, this would come from actual Aave yield

        // Try to mint to this contract
        // Note: This requires the asset to have a mint function
        // We'll handle this in the deployment script by granting mint permissions
        (bool success, ) = address(asset).call(abi.encodeWithSignature("mint(address,uint256)", address(this), amount));
        require(success, "Yield mint failed - check asset mint permissions");

        accumulatedYield += amount;

        emit YieldAdded(amount, block.timestamp);
    }

    /**
     * @notice Get available yield (total yield minus principal)
     * @return Available yield amount
     */
    function getAvailableYield() external view returns (uint256) {
        uint256 totalAssets = asset.balanceOf(address(this));
        if (totalAssets <= totalSupply) {
            return 0;
        }
        return totalAssets - totalSupply;
    }

    /**
     * @notice Harvest yield (withdraw only the yield, keep principal)
     * @param to Address to send yield to
     * @return Yield amount harvested
     */
    function harvestYield(address to) external onlyOwner returns (uint256) {
        uint256 availableYield = this.getAvailableYield();
        require(availableYield > 0, "No yield available");
        require(to != address(0), "Invalid to address");

        // Transfer only the yield, keep principal in pool
        require(asset.transfer(to, availableYield), "Yield transfer failed");

        return availableYield;
    }

    /**
     * @notice Get total assets in pool (principal + yield)
     * @return Total assets
     */
    function getTotalAssets() external view returns (uint256) {
        return asset.balanceOf(address(this));
    }
}

contracts/MockUSDC.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title MockUSDC
 * @notice Test USDC token for Endaoment platform testing
 * @dev Mimics USDC with 6 decimals and allows free minting for testing
 */
contract MockUSDC is ERC20, Ownable {
    // Authorized minters (for yield simulation)
    mapping(address => bool) public minters;

    event MinterAdded(address indexed minter);
    event MinterRemoved(address indexed minter);

    modifier onlyMinter() {
        require(msg.sender == owner() || minters[msg.sender], "Not authorized to mint");
        _;
    }

    /**
     * @notice Initialize MockUSDC with 1M initial supply
     * @dev Mints 1,000,000 USDC (with 6 decimals) to deployer
     */
    constructor() ERC20("Mock USDC", "USDC") Ownable(msg.sender) {
        // Mint 1M USDC to deployer (1_000_000 * 10^6)
        _mint(msg.sender, 1_000_000 * 10 ** 6);
    }

    /**
     * @notice Returns 6 decimals to match real USDC
     * @return Number of decimals (6)
     */
    function decimals() public pure override returns (uint8) {
        return 6;
    }

    /**
     * @notice Add an address as authorized minter
     * @param minter Address to grant minting permission
     */
    function addMinter(address minter) external onlyOwner {
        require(minter != address(0), "Invalid minter address");
        minters[minter] = true;
        emit MinterAdded(minter);
    }

    /**
     * @notice Remove minting permission from an address
     * @param minter Address to revoke minting permission
     */
    function removeMinter(address minter) external onlyOwner {
        minters[minter] = false;
        emit MinterRemoved(minter);
    }

    /**
     * @notice Mint new tokens to any address for testing
     * @param to Address to mint tokens to
     * @param amount Amount of tokens to mint (in 6 decimal format)
     */
    function mint(address to, uint256 amount) external onlyMinter {
        _mint(to, amount);
    }

    /**
     * @notice Faucet function for testing - mint 10000 USDC to caller
     * @dev Anyone can call this for testing purposes
     */
    function faucet() external {
        _mint(msg.sender, 10_000 * 10 ** 6); // 10000 USDC
    }
}

contracts/MyGovernor.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/governance/Governor.sol";
import "@openzeppelin/contracts/governance/extensions/GovernorSettings.sol";
import "@openzeppelin/contracts/governance/extensions/GovernorCountingSimple.sol";
import "@openzeppelin/contracts/governance/extensions/GovernorVotes.sol";
import "@openzeppelin/contracts/governance/extensions/GovernorVotesQuorumFraction.sol";
import "@openzeppelin/contracts/governance/extensions/GovernorTimelockControl.sol";
import "./StudentSBT.sol";

/**
 * @title MyGovernor
 * @notice Optimistic Governance with Veto for Student DAO
 * @dev Uses StudentSBT for voting, 0 delay, 5-10 min voting period, Timelock with veto
 */
contract MyGovernor is
    Governor,
    GovernorSettings,
    GovernorCountingSimple,
    GovernorVotes,
    GovernorVotesQuorumFraction,
    GovernorTimelockControl
{
    /**
     * @notice Initialize MyGovernor
     * @param _token StudentSBT token for voting
     * @param _timelock TimelockController address
     * @param _votingDelay Voting delay in blocks (0 for instant)
     * @param _votingPeriod Voting period in blocks (5-10 mins for demo)
     * @param _quorumPercentage Quorum percentage (e.g., 4 = 4%)
     */
    constructor(
        StudentSBT _token,
        TimelockController _timelock,
        uint48 _votingDelay,
        uint32 _votingPeriod,
        uint256 _quorumPercentage
    )
        Governor("Student DAO Governor")
        GovernorSettings(_votingDelay, _votingPeriod, 0) // 0 = no proposal threshold
        GovernorVotes(IVotes(address(_token)))
        GovernorVotesQuorumFraction(_quorumPercentage)
        GovernorTimelockControl(_timelock)
    {}

    // The following functions are overrides required by Solidity.

    function votingDelay() public view override(Governor, GovernorSettings) returns (uint256) {
        return super.votingDelay();
    }

    function votingPeriod() public view override(Governor, GovernorSettings) returns (uint256) {
        return super.votingPeriod();
    }

    function quorum(uint256 blockNumber) public view override(Governor, GovernorVotesQuorumFraction) returns (uint256) {
        return super.quorum(blockNumber);
    }

    function state(uint256 proposalId) public view override(Governor, GovernorTimelockControl) returns (ProposalState) {
        return super.state(proposalId);
    }

    function propose(
        address[] memory targets,
        uint256[] memory values,
        bytes[] memory calldatas,
        string memory description
    ) public override(Governor) returns (uint256) {
        return super.propose(targets, values, calldatas, description);
    }

    function proposalThreshold() public view override(Governor, GovernorSettings) returns (uint256) {
        return super.proposalThreshold();
    }

    function proposalNeedsQueuing(
        uint256 proposalId
    ) public view override(Governor, GovernorTimelockControl) returns (bool) {
        return super.proposalNeedsQueuing(proposalId);
    }

    function _queueOperations(
        uint256 proposalId,
        address[] memory targets,
        uint256[] memory values,
        bytes[] memory calldatas,
        bytes32 descriptionHash
    ) internal override(Governor, GovernorTimelockControl) returns (uint48) {
        return super._queueOperations(proposalId, targets, values, calldatas, descriptionHash);
    }

    function _executeOperations(
        uint256 proposalId,
        address[] memory targets,
        uint256[] memory values,
        bytes[] memory calldatas,
        bytes32 descriptionHash
    ) internal override(Governor, GovernorTimelockControl) {
        super._executeOperations(proposalId, targets, values, calldatas, descriptionHash);
    }

    function _cancel(
        address[] memory targets,
        uint256[] memory values,
        bytes[] memory calldatas,
        bytes32 descriptionHash
    ) internal override(Governor, GovernorTimelockControl) returns (uint256) {
        return super._cancel(targets, values, calldatas, descriptionHash);
    }

    function _executor() internal view override(Governor, GovernorTimelockControl) returns (address) {
        return super._executor();
    }
}

