// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./interfaces/IStudentRegistry.sol";

/**
 * @title StudentRegistry
 * @notice Manages verified student profiles for the Endaoment platform
 * @dev Only owner (admin) can add/deactivate students, AllocationManager records funding
 */
contract StudentRegistry is IStudentRegistry, Ownable {
    // Student data mapping
    mapping(address => Student) private students;

    // Array of all student addresses for enumeration
    address[] private studentAddresses;

    // Mapping to check if address is registered
    mapping(address => bool) private isRegistered;

    // AllocationManager address (can record funding)
    address public allocationManager;

    // StudentSBT contract address (for auto-minting)
    address public studentSBT;

    /**
     * @notice Initialize StudentRegistry
     */
    constructor() Ownable(msg.sender) {}

    /**
     * @notice Set the AllocationManager contract address
     * @param _allocationManager Address of AllocationManager contract
     */
    function setAllocationManager(address _allocationManager) external onlyOwner {
        require(_allocationManager != address(0), "Invalid allocation manager address");
        allocationManager = _allocationManager;
    }

    /**
     * @notice Set the StudentSBT contract address
     * @param _studentSBT Address of StudentSBT contract
     */
    function setStudentSBT(address _studentSBT) external onlyOwner {
        require(_studentSBT != address(0), "Invalid StudentSBT address");
        studentSBT = _studentSBT;
    }

    /**
     * @notice Add a new student to the registry
     * @param studentAddress Student's wallet address
     * @param name Student's full name
     * @param university University or institution
     * @param researchArea Field of research
     */
    function addStudent(
        address studentAddress,
        string calldata name,
        string calldata university,
        string calldata researchArea
    ) external onlyOwner {
        require(studentAddress != address(0), "Invalid student address");
        require(!isRegistered[studentAddress], "Student already registered");
        require(bytes(name).length > 0, "Name cannot be empty");
        require(bytes(university).length > 0, "University cannot be empty");
        require(bytes(researchArea).length > 0, "Research area cannot be empty");

        students[studentAddress] = Student({
            wallet: studentAddress,
            name: name,
            university: university,
            researchArea: researchArea,
            isActive: true,
            totalReceived: 0,
            addedAt: block.timestamp
        });

        studentAddresses.push(studentAddress);
        isRegistered[studentAddress] = true;

        emit StudentAdded(studentAddress, name, university);
    }

    /**
     * @notice Get student profile by address
     * @param studentAddress Student's wallet address
     * @return Student struct with all profile data
     */
    function getStudent(address studentAddress) external view returns (Student memory) {
        require(isRegistered[studentAddress], "Student not found");
        return students[studentAddress];
    }

    /**
     * @notice Get all registered student addresses
     * @return Array of all student addresses
     */
    function getAllStudents() external view returns (address[] memory) {
        return studentAddresses;
    }

    /**
     * @notice Get all active student addresses
     * @return Array of active student addresses
     */
    function getActiveStudents() external view returns (address[] memory) {
        uint256 activeCount = 0;

        // Count active students
        for (uint256 i = 0; i < studentAddresses.length; i++) {
            if (students[studentAddresses[i]].isActive) {
                activeCount++;
            }
        }

        // Build array of active students
        address[] memory activeStudents = new address[](activeCount);
        uint256 currentIndex = 0;

        for (uint256 i = 0; i < studentAddresses.length; i++) {
            if (students[studentAddresses[i]].isActive) {
                activeStudents[currentIndex] = studentAddresses[i];
                currentIndex++;
            }
        }

        return activeStudents;
    }

    /**
     * @notice Deactivate a student (stop accepting funding)
     * @param studentAddress Student's wallet address
     */
    function deactivateStudent(address studentAddress) external onlyOwner {
        require(isRegistered[studentAddress], "Student not found");
        require(students[studentAddress].isActive, "Student already inactive");

        students[studentAddress].isActive = false;

        emit StudentDeactivated(studentAddress);
    }

    /**
     * @notice Reactivate a deactivated student
     * @param studentAddress Student's wallet address
     */
    function reactivateStudent(address studentAddress) external onlyOwner {
        require(isRegistered[studentAddress], "Student not found");
        require(!students[studentAddress].isActive, "Student already active");

        students[studentAddress].isActive = true;

        emit StudentReactivated(studentAddress);
    }

    /**
     * @notice Record funding received by a student
     * @dev Can only be called by AllocationManager during distribution
     * @param studentAddress Student's wallet address
     * @param amount Amount of USDC received
     */
    function recordFunding(address studentAddress, uint256 amount) external {
        require(msg.sender == allocationManager, "Only AllocationManager can record funding");
        require(isRegistered[studentAddress], "Student not found");

        students[studentAddress].totalReceived += amount;

        emit FundingRecorded(studentAddress, amount);
    }

    /**
     * @notice Check if a student is active
     * @param studentAddress Student's wallet address
     * @return Boolean indicating if student is active
     */
    function isStudentActive(address studentAddress) external view returns (bool) {
        if (!isRegistered[studentAddress]) {
            return false;
        }
        return students[studentAddress].isActive;
    }

    /**
     * @notice Get total number of registered students
     * @return Number of students
     */
    function getStudentCount() external view returns (uint256) {
        return studentAddresses.length;
    }

    /**
     * @notice Public function for students to register themselves
     * @param name Student's full name
     * @param university University or institution
     * @param researchArea Field of research (can be faculty/career)
     * @param studentId Student ID or enrollment number
     * @param academicAchievements Number of academic achievements (0-10)
     * @param sportsAchievements Number of sports achievements (0-10)
     * @param studentAchievements Number of student achievements (0-10)
     */
    function registerStudent(
        string calldata name,
        string calldata university,
        string calldata researchArea,
        string calldata studentId,
        uint256 academicAchievements,
        uint256 sportsAchievements,
        uint256 studentAchievements
    ) external {
        address studentAddress = msg.sender;
        require(!isRegistered[studentAddress], "Student already registered");
        require(bytes(name).length > 0, "Name cannot be empty");
        require(bytes(university).length > 0, "University cannot be empty");
        require(bytes(researchArea).length > 0, "Research area cannot be empty");
        require(bytes(studentId).length > 0, "Student ID cannot be empty");
        require(academicAchievements <= 10, "Academic achievements max 10");
        require(sportsAchievements <= 10, "Sports achievements max 10");
        require(studentAchievements <= 10, "Student achievements max 10");

        students[studentAddress] = Student({
            wallet: studentAddress,
            name: name,
            university: university,
            researchArea: researchArea,
            isActive: true,
            totalReceived: 0,
            addedAt: block.timestamp
        });

        studentAddresses.push(studentAddress);
        isRegistered[studentAddress] = true;

        emit StudentAdded(studentAddress, name, university);

        // Auto-mint StudentSBT if contract is set
        if (studentSBT != address(0)) {
            // Call StudentSBT.mint() - this will revert if student already has SBT
            (bool success, ) = studentSBT.call(abi.encodeWithSignature("mint(address)", studentAddress));
            // Don't revert if mint fails - student is still registered
            if (!success) {
                // Log but continue
            }
        }
    }
}

contracts/StudentSBT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/governance/utils/IVotes.sol";
import "@openzeppelin/contracts/utils/math/SafeCast.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

/**
 * @title StudentSBT
 * @notice Soulbound Token (SBT) for student governance participation
 * @dev Non-transferable ERC721 - students use this to vote in the DAO
 * @dev Implements IVotes for OpenZeppelin Governor compatibility
 */
contract StudentSBT is ERC721, Ownable, ReentrancyGuard, IVotes {
    // Checkpoint struct for voting power tracking
    struct Checkpoint {
        uint32 fromBlock;
        uint224 votes;
    }

    // Token counter
    uint256 private _tokenIdCounter;

    // Registry of authorized minters (only admin can mint)
    mapping(address => bool) public authorizedMinters;

    // Mapping: student address => tokenId
    mapping(address => uint256) public studentToTokenId;

    // Mapping: tokenId => student address
    mapping(uint256 => address) public tokenIdToStudent;

    // IVotes: Checkpoints for voting power (1 token = 1 vote)
    mapping(address => Checkpoint[]) private _checkpoints;
    Checkpoint[] private _totalSupplyCheckpoints;

    // Events
    event AuthorizedMinterAdded(address indexed minter);
    event AuthorizedMinterRemoved(address indexed minter);
    event StudentRegistered(address indexed student, uint256 indexed tokenId);

    modifier onlyAuthorizedMinter() {
        require(authorizedMinters[msg.sender] || msg.sender == owner(), "Not authorized to mint");
        _;
    }

    /**
     * @notice Initialize StudentSBT
     * @param name Token name
     * @param symbol Token symbol
     */
    constructor(string memory name, string memory symbol) ERC721(name, symbol) Ownable(msg.sender) {
        _tokenIdCounter = 1; // Start from token ID 1
    }

    /**
     * @notice Add an authorized minter (e.g., StudentRegistry)
     * @param minter Address to grant minting permission
     */
    function addAuthorizedMinter(address minter) external onlyOwner {
        require(minter != address(0), "Invalid minter address");
        authorizedMinters[minter] = true;
        emit AuthorizedMinterAdded(minter);
    }

    /**
     * @notice Remove minting permission
     * @param minter Address to revoke minting permission
     */
    function removeAuthorizedMinter(address minter) external onlyOwner {
        authorizedMinters[minter] = false;
        emit AuthorizedMinterRemoved(minter);
    }

    /**
     * @notice Mint SBT to a student (only authorized minters)
     * @param student Address of the student
     * @return tokenId The minted token ID
     */
    function mint(address student) external onlyAuthorizedMinter nonReentrant returns (uint256) {
        require(student != address(0), "Invalid student address");
        require(studentToTokenId[student] == 0, "Student already has SBT");

        uint256 tokenId = _tokenIdCounter;
        _tokenIdCounter++;

        _safeMint(student, tokenId);

        studentToTokenId[student] = tokenId;
        tokenIdToStudent[tokenId] = student;

        // Update voting power checkpoint (1 SBT = 1 vote)
        _writeCheckpoint(_checkpoints[student], _add, 1);
        _writeCheckpoint(_totalSupplyCheckpoints, _add, 1);

        emit StudentRegistered(student, tokenId);

        return tokenId;
    }

    /**
     * @notice Get token ID for a student address
     * @param student Student address
     * @return tokenId The student's token ID (0 if not minted)
     */
    function getTokenId(address student) external view returns (uint256) {
        return studentToTokenId[student];
    }

    /**
     * @notice Check if an address has an SBT
     * @param student Address to check
     * @return True if student has an SBT
     */
    function hasSBT(address student) external view returns (bool) {
        return studentToTokenId[student] != 0 && _ownerOf(studentToTokenId[student]) == student;
    }

    /**
     * @notice Get total number of minted SBTs
     * @return Total supply
     */
    function totalSupply() external view returns (uint256) {
        return _tokenIdCounter - 1;
    }

    /**
     * @notice Override transfer functions to make token Soulbound (non-transferable)
     * @dev All transfer functions revert to enforce Soulbound nature
     */
    function transferFrom(address, address, uint256) public pure override {
        revert("SBT: Token is non-transferable");
    }

    function safeTransferFrom(address, address, uint256, bytes memory) public pure override {
        revert("SBT: Token is non-transferable");
    }

    function approve(address, uint256) public pure override {
        revert("SBT: Token is non-transferable");
    }

    function setApprovalForAll(address, bool) public pure override {
        revert("SBT: Token is non-transferable");
    }

    function getApproved(uint256) public pure override returns (address) {
        return address(0);
    }

    function isApprovedForAll(address, address) public pure override returns (bool) {
        return false;
    }

    // ============ IVotes Implementation ============

    /**
     * @notice Get voting power for an account at current block
     * @param account Account to check
     * @return Voting power (1 if has SBT, 0 otherwise)
     */
    function getVotes(address account) public view override returns (uint256) {
        return (studentToTokenId[account] != 0 && _ownerOf(studentToTokenId[account]) == account) ? 1 : 0;
    }

    /**
     * @notice Get voting power for an account at a specific block
     * @param account Account to check
     * @param blockNumber Block number to check
     * @return Voting power
     */
    function getPastVotes(address account, uint256 blockNumber) public view override returns (uint256) {
        require(blockNumber < block.number, "SBT: block not yet mined");
        return _checkpointsLookup(_checkpoints[account], blockNumber);
    }

    /**
     * @notice Get total voting power at current block
     * @return Total voting power
     */
    function getTotalSupply() public view returns (uint256) {
        return _tokenIdCounter - 1;
    }

    /**
     * @notice Get total voting power at a specific block
     * @param blockNumber Block number to check
     * @return Total voting power
     */
    function getPastTotalSupply(uint256 blockNumber) public view override returns (uint256) {
        require(blockNumber < block.number, "SBT: block not yet mined");
        return _checkpointsLookup(_totalSupplyCheckpoints, blockNumber);
    }

    /**
     * @notice Delegate voting power (not used for SBT, but required by IVotes)
     */
    function delegates(address) public pure override returns (address) {
        return address(0); // SBT doesn't support delegation
    }

    /**
     * @notice Delegate votes (not used for SBT)
     */
    function delegate(address) public pure override {
        revert("SBT: Delegation not supported");
    }

    /**
     * @notice Delegate votes by signature (not used for SBT)
     */
    function delegateBySig(address, uint256, uint256, uint8, bytes32, bytes32) public pure override {
        revert("SBT: Delegation not supported");
    }

    // ============ Internal Checkpoint Functions ============

    function _writeCheckpoint(
        Checkpoint[] storage ckpts,
        function(uint256, uint256) view returns (uint256) op,
        uint256 delta
    ) private returns (uint256 oldWeight, uint256 newWeight) {
        uint256 pos = ckpts.length;
        oldWeight = pos == 0 ? 0 : ckpts[pos - 1].votes;
        newWeight = op(oldWeight, delta);

        if (pos > 0 && ckpts[pos - 1].fromBlock == block.number) {
            ckpts[pos - 1].votes = SafeCast.toUint224(newWeight);
        } else {
            ckpts.push(
                Checkpoint({ fromBlock: SafeCast.toUint32(block.number), votes: SafeCast.toUint224(newWeight) })
            );
        }
    }

    function _add(uint256 a, uint256 b) private pure returns (uint256) {
        return a + b;
    }

    function _subtract(uint256 a, uint256 b) private pure returns (uint256) {
        return a - b;
    }

    function _checkpointsLookup(Checkpoint[] storage ckpts, uint256 blockNumber) private view returns (uint256) {
        // We run a binary search to look for the earliest checkpoint taken after `blockNumber`.
        //
        // During the loop, the index of the wanted checkpoint remains in the range [low-1, high).
        // With each iteration, either `low` or `high` is moved towards the middle of the range to maintain the invariant.
        // - If the middle checkpoint is after `blockNumber`, we look in [low, mid)
        // - If the middle checkpoint is before or equal to `blockNumber`, we look in [mid+1, high)
        // Once we reach a single value (when low == high), we've found the right checkpoint at the index high-1, if not
        // out of bounds.
        uint256 high = ckpts.length;
        uint256 low = 0;
        while (low < high) {
            uint256 mid = Math.average(low, high);
            if (ckpts[mid].fromBlock > blockNumber) {
                high = mid;
            } else {
                low = mid + 1;
            }
        }

        return high == 0 ? 0 : ckpts[high - 1].votes;
    }
}

contracts/UniversityRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title UniversityRegistry
 * @notice Manages university registrations and wallet addresses
 * @dev Wallets can be locked for MVP to prevent changes
 */
contract UniversityRegistry is Ownable {
    struct University {
        string name;
        address wallet;
        bool isActive;
        bool isLocked; // For MVP: once locked, cannot be changed
        uint256 registeredAt;
    }

    // Mapping: university ID => University struct
    mapping(string => University) public universities;

    // Mapping: wallet address => university ID
    mapping(address => string) public walletToUniversityId;

    // Array of all university IDs
    string[] public universityIds;

    // Events
    event UniversityRegistered(string indexed universityId, string name, address indexed wallet, uint256 timestamp);

    event UniversityWalletUpdated(string indexed universityId, address indexed oldWallet, address indexed newWallet);

    event UniversityLocked(string indexed universityId, address indexed wallet);
    event UniversityActivated(string indexed universityId);
    event UniversityDeactivated(string indexed universityId);

    modifier onlyUnlocked(string memory universityId) {
        require(!universities[universityId].isLocked, "University wallet is locked for MVP");
        _;
    }

    /**
     * @notice Initialize UniversityRegistry
     */
    constructor() Ownable(msg.sender) {}

    /**
     * @notice Register a new university
     * @param universityId Unique identifier (e.g., "unam", "ibero")
     * @param name Full name of the university
     * @param wallet Wallet address to receive yield
     */
    function registerUniversity(string memory universityId, string memory name, address wallet) external onlyOwner {
        require(bytes(universityId).length > 0, "University ID cannot be empty");
        require(bytes(name).length > 0, "University name cannot be empty");
        require(wallet != address(0), "Invalid wallet address");
        require(bytes(universities[universityId].name).length == 0, "University already registered");
        require(bytes(walletToUniversityId[wallet]).length == 0, "Wallet already registered");

        universities[universityId] = University({
            name: name,
            wallet: wallet,
            isActive: true,
            isLocked: false,
            registeredAt: block.timestamp
        });

        walletToUniversityId[wallet] = universityId;
        universityIds.push(universityId);

        emit UniversityRegistered(universityId, name, wallet, block.timestamp);
    }

    /**
     * @notice Update university wallet (only if not locked)
     * @param universityId University identifier
     * @param newWallet New wallet address
     */
    function updateUniversityWallet(
        string memory universityId,
        address newWallet
    ) external onlyOwner onlyUnlocked(universityId) {
        require(bytes(universities[universityId].name).length > 0, "University not found");
        require(newWallet != address(0), "Invalid wallet address");
        require(bytes(walletToUniversityId[newWallet]).length == 0, "Wallet already registered");

        address oldWallet = universities[universityId].wallet;
        delete walletToUniversityId[oldWallet];
        walletToUniversityId[newWallet] = universityId;
        universities[universityId].wallet = newWallet;

        emit UniversityWalletUpdated(universityId, oldWallet, newWallet);
    }

    /**
     * @notice Lock university wallet (for MVP - prevents changes)
     * @param universityId University identifier
     */
    function lockUniversityWallet(string memory universityId) external onlyOwner {
        require(bytes(universities[universityId].name).length > 0, "University not found");
        require(!universities[universityId].isLocked, "University already locked");

        universities[universityId].isLocked = true;

        emit UniversityLocked(universityId, universities[universityId].wallet);
    }

    /**
     * @notice Activate a university
     * @param universityId University identifier
     */
    function activateUniversity(string memory universityId) external onlyOwner {
        require(bytes(universities[universityId].name).length > 0, "University not found");
        require(!universities[universityId].isActive, "University already active");

        universities[universityId].isActive = true;

        emit UniversityActivated(universityId);
    }

    /**
     * @notice Deactivate a university
     * @param universityId University identifier
     */
    function deactivateUniversity(string memory universityId) external onlyOwner {
        require(bytes(universities[universityId].name).length > 0, "University not found");
        require(universities[universityId].isActive, "University already inactive");

        universities[universityId].isActive = false;

        emit UniversityDeactivated(universityId);
    }

    /**
     * @notice Get university by ID
     * @param universityId University identifier
     * @return University struct
     */
    function getUniversity(string memory universityId) external view returns (University memory) {
        require(bytes(universities[universityId].name).length > 0, "University not found");
        return universities[universityId];
    }

    /**
     * @notice Get university ID by wallet address
     * @param wallet Wallet address
     * @return University ID
     */
    function getUniversityIdByWallet(address wallet) external view returns (string memory) {
        require(bytes(walletToUniversityId[wallet]).length > 0, "Wallet not registered");
        return walletToUniversityId[wallet];
    }

    /**
     * @notice Get all university IDs
     * @return Array of university IDs
     */
    function getAllUniversityIds() external view returns (string[] memory) {
        return universityIds;
    }

    /**
     * @notice Get all active university IDs
     * @return Array of active university IDs
     */
    function getActiveUniversityIds() external view returns (string[] memory) {
        uint256 activeCount = 0;

        // Count active universities
        for (uint256 i = 0; i < universityIds.length; i++) {
            if (universities[universityIds[i]].isActive) {
                activeCount++;
            }
        }

        // Build array
        string[] memory activeIds = new string[](activeCount);
        uint256 currentIndex = 0;

        for (uint256 i = 0; i < universityIds.length; i++) {
            if (universities[universityIds[i]].isActive) {
                activeIds[currentIndex] = universityIds[i];
                currentIndex++;
            }
        }

        return activeIds;
    }
}

contracts/YieldSplitter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title YieldSplitter
 * @notice Splits yield 50/50 between University Wallet and TimelockController (DAO Treasury)
 * @dev Simple contract that receives USDC yield and splits it immediately
 */
contract YieldSplitter is Ownable, ReentrancyGuard {
    // Asset token (USDC)
    IERC20 public immutable asset;

    // University wallet (multisig for operational expenses)
    address public universityWallet;

    // TimelockController address (DAO treasury)
    address public timelockController;

    // Split ratio (50% = 5000 bps)
    uint256 public constant SPLIT_BPS = 5000; // 50%
    uint256 public constant BPS_DENOMINATOR = 10000;

    // Events
    event YieldSplit(
        uint256 totalAmount,
        uint256 universityAmount,
        uint256 daoAmount,
        address indexed universityWallet,
        address indexed timelockController
    );
    event UniversityWalletUpdated(address indexed oldWallet, address indexed newWallet);
    event TimelockControllerUpdated(address indexed oldTimelock, address indexed newTimelock);

    /**
     * @notice Initialize YieldSplitter
     * @param _asset Address of the asset token (USDC)
     * @param _universityWallet Address of university multisig wallet
     * @param _timelockController Address of TimelockController (DAO treasury)
     */
    constructor(address _asset, address _universityWallet, address _timelockController) Ownable(msg.sender) {
        require(_asset != address(0), "Invalid asset address");
        require(_universityWallet != address(0), "Invalid university wallet");
        require(_timelockController != address(0), "Invalid timelock controller");

        asset = IERC20(_asset);
        universityWallet = _universityWallet;
        timelockController = _timelockController;
    }

    /**
     * @notice Split received yield 50/50 between University and DAO
     * @dev Can be called by anyone, but typically called by Vault after harvesting yield
     */
    function splitYield() external nonReentrant {
        uint256 balance = asset.balanceOf(address(this));
        require(balance > 0, "No yield to split");

        // Calculate 50/50 split
        uint256 universityAmount = (balance * SPLIT_BPS) / BPS_DENOMINATOR;
        uint256 daoAmount = balance - universityAmount; // Handle rounding

        // Transfer to University Wallet
        require(asset.transfer(universityWallet, universityAmount), "University transfer failed");

        // Transfer to TimelockController (DAO Treasury)
        require(asset.transfer(timelockController, daoAmount), "DAO transfer failed");

        emit YieldSplit(balance, universityAmount, daoAmount, universityWallet, timelockController);
    }

    /**
     * @notice Update university wallet address
     * @param _universityWallet New university wallet address
     */
    function setUniversityWallet(address _universityWallet) external onlyOwner {
        require(_universityWallet != address(0), "Invalid university wallet");
        address oldWallet = universityWallet;
        universityWallet = _universityWallet;
        emit UniversityWalletUpdated(oldWallet, _universityWallet);
    }

    /**
     * @notice Update TimelockController address
     * @param _timelockController New TimelockController address
     */
    function setTimelockController(address _timelockController) external onlyOwner {
        require(_timelockController != address(0), "Invalid timelock controller");
        address oldTimelock = timelockController;
        timelockController = _timelockController;
        emit TimelockControllerUpdated(oldTimelock, _timelockController);
    }

    /**
     * @notice Get current yield balance in splitter
     * @return Current balance
     */
    function getYieldBalance() external view returns (uint256) {
        return asset.balanceOf(address(this));
    }
}

